mapper.insert:
INSERT INTO POST
(
MEMBER_CODE
, POST_TITLE
, POST_CONTEXT
, MAIN_CATEGORY
, SUB_CATEGORY
, POST_DATE
, VEF_STATUS
)
VALUES
(
#{memberCode}
, #{postTitle}
, #{postContext}
, #{mainCategory}
, #{subCategory}
, DATE_FORMAT(now(), '%y/%m/%d')
, NULL
)

mapper.update:
UPDATE POST
SET
POST_TITLE = #{ postTitle },
POST_CONTEXT = #{ postContext }
WHERE POST_CODE = #{ postCode } AND SUB_CATEGORY = #{ subCategory }
